mapper.select:
select

from course_roster


    and course_id = #{courseId,jdbcType=BIGINT}


    and account_id = #{accountId,jdbcType=BIGINT}


    and roster_name = #{rosterName,jdbcType=VARCHAR}


    and roster_course_count_rest = #{rosterCourseCountRest,jdbcType=INTEGER}


    and roster_seat_x = #{rosterSeatX,jdbcType=INTEGER}


    and roster_seat_y = #{rosterSeatY,jdbcType=INTEGER}

select

FROM roster_attendance ra LEFT JOIN class_course cc ON ra.`course_id`=cc.`course_id`
where ra.course_id = #{courseId,jdbcType=BIGINT} and ra.attend_type=2 AND ra.attend_section_num=#{courseCurrent}
