mapper.select:
select 

from refund_order
where refund_id = #{refundId,jdbcType=BIGINT}

  and account_id = #{accountId,jdbcType=BIGINT}

select

from refund_order
where order_id = #{orderId,jdbcType=BIGINT}

  and account_id = #{accountId,jdbcType=BIGINT}

select

from refund_order ro
inner join user u ON ro.account_id=u.account_id


    and ro.order_id = #{orderId,jdbcType=BIGINT}


    and ro.course_id = #{courseId,jdbcType=BIGINT}


    and ro.account_id = #{accountId,jdbcType=BIGINT}


    and ro.amount = #{amount,jdbcType=DECIMAL}


    and ro.actual_amount = #{actualAmount,jdbcType=DECIMAL}


    and ro.course_amount = #{courseAmount,jdbcType=DECIMAL}


    and ro.order_pay_way = #{orderPayWay,jdbcType=CHAR}


    and ro.course_name = #{courseName,jdbcType=VARCHAR}


    and ro.course_total = #{courseTotal,jdbcType=INTEGER}


    and ro.refund_course_total = #{refundCourseTotal,jdbcType=INTEGER}


    and ro.refund_status = #{refundStatus,jdbcType=CHAR}
